        if (this.faceBookSelected) {
          arr.push('Facebook');
        }
        params['channels'] = arr.toString();
      }
      this.marketingCampaignChannelsApiService.CampaignChannelListPUT(this.definePostObject['campaignId'], params).subscribe(res => {}, error => {});
