      that.deviceIps = [];
      that.deleteDevices = [];
      if (isChecked) {
        $('input[name^="delete_device_id"]').each(function () {
          if (i >= tot) {
            return false;
          }
          $(this).prop('checked', true);
          var classes = $(this).attr('class').split(' ');
          let id = classes[0].substring('delete_device_id_'.length);
          let d = that.deviceData.filter(el => el._id === id);
          let ip = '';
