            ip = d[0].deviceIpV4;
          } else {
            ip = d[0].deviceIpV6;
          }
          that.deleteIds.push(id);
          that.deviceIps.push(ip);
          that.deleteDevices.push(d[0]);
          i++;
        });
      } else {
        $('input[name^="delete_device_id"]').each(function () {
