      $(".next").text(isFrench ? 'Suivant' : 'Next');
      $(".last").text(isFrench ? 'Dernière' : 'Last');
      $(".dataTables_empty").text(isFrench ? 'Pas de données disponibles dans la table' : 'No data available in table');
