    usageByAppDataForming(array, page) {
      let returnArray = [];
      let obj = {};
      let totals = this.getArraySum(array.map(el => el.originalValue));
      array.forEach(el => {
        returnArray.push({
          [this.language['CHANNEL NAME']]: el.channelName,
          [this.language['CHANNEL SIZE']]: el.channelSize
        });
      });
      return returnArray;
    }
    getArraySum(value) {
      let arrayData = value;
      if (typeof arrayData == 'object') {
        arrayData = Object.values(value);
      }
      let sum = value.reduce(function (a, b) {
        return a + b;
      }, 0);
      return sum;
    }
