    onPrev() {
      this.currActive--;
      if (this.radio1 === "Escalated") {
        this.currActive = 0;
      }
      if (this.radio2 === "User owned equipment" && this.currActive != 0) {
        this.currActive = 1;
      }
      this.displaysubmit = false;
    }
    statusoption() {
      if (this.radio1 === "Resolved") {
        this.status = [this.language["WAN"], this.language["Gateway"], this.language["Wi-Fi"], this.language["Client Devices"]];
        this.radio2 = this.status[0];
      } else if (this.radio1 === "Escalated") {
        this.currActive = 3;
        this.displaysubmit = true;
        this.radio2 = "";
        this.radio3 = "";
      } else if (this.radio1 === "Truck roll") {
        this.status = [this.language["Hardware issue"], this.language["User owned equipment"]];
        this.radio2 = this.status[0];
      } else if (this.radio1 === "Account Inquiry") {
        this.status = [this.language["Customer in-person"], this.language["Customer on phone"], this.language["Customer not present"], this.language["Call center escalation"]];
        this.radio2 = this.status[0];
      }
    }
    onNext() {
      this.currActive++;
      // this.radio3 = this.subreason[0];
      //this.radio2 = this.status[0];
      if (this.currActive == 3) {
        this.displaysubmit = true;
      }
      if (this.currActive === 1) {
        if (this.radio1 === "Resolved") {
          this.status = ["WAN", "Gateway", "Wi-Fi", "Client Devices"];
          this.radio2 = this.status[0];
        } else if (this.radio1 === "Escalated") {
          this.currActive = 3;
          this.displaysubmit = true;
          this.radio2 = "";
          this.radio3 = "";
        } else if (this.radio1 === "Truck roll") {
          this.status = ["Hardware issue", "User owned equipment"];
          this.radio2 = this.status[0];
        } else if (this.radio1 === "Account Inquiry") {
          this.status = ["Customer in-person", "Customer on phone", "Customer not present", "Call center escalation"];
          this.radio2 = this.status[0];
        }
      }
      if (this.currActive === 2) {
        switch (this.radio2) {
          case "WAN":
            this.subreason = ["Saturation", "Poor-speed", "No-speed"];
            this.radio3 = this.subreason[0];
            break;
          case "Gateway":
            this.subreason = ["Offline", "Reboot", "Upgrade", "Replacement"];
            this.radio3 = this.subreason[0];
            break;
          case "Wi-Fi":
            this.subreason = ["SSID/Password", "Interference", "Coverage"];
            this.radio3 = this.subreason[0];
            break;
          case "Client Devices":
            this.subreason = ["Legacy devices", "Low signal strength", "Saturation"];
            this.radio3 = this.subreason[0];
            break;
          case "Hardware issue":
            this.subreason = ["Wireless Mesh/Improved Coverage", "Device replacement", "OSS/BSS programming issue", "End of life device replacement", "Other"];
            this.radio3 = this.subreason[0];
            break;
          case "User owned equipment":
            this.currActive = 3;
            this.displaysubmit = true;
            this.radio3 = "";
            break;
          case "Customer in-person":
            this.subreason = ["Customer education", "Training", "Upsell", "Proactive/outbound call", "Trouble ticket review"];
            this.radio3 = this.subreason[0];
            break;
          case "Customer on phone":
            this.subreason = ["Customer education", "Training", "Upsell", "Proactive/outbound call", "Trouble ticket review"];
            this.radio3 = this.subreason[0];
            break;
          case "Customer not present":
            this.subreason = ["Customer education", "Training", "Upsell", "Proactive/outbound call", "Trouble ticket review"];
            this.radio3 = this.subreason[0];
            break;
          case "Call center escalation":
            this.subreason = ["Customer education", "Training", "Upsell", "Proactive/outbound call", "Trouble ticket review"];
            this.radio3 = this.subreason[0];
            break;
        }
      }
      //  switch(this.radio3)
      //  {
      //    default: this.displaysubmit=true;
      //  }
    }
