    timeConvert(time) {
      let d = Math.floor(time / 1440); // 60*24
      let h = Math.floor((time - d * 1440) / 60);
      let m = Math.round(time % 60);
      if (d > 0) {
        return d + " Days " + h + " Hrs , " + m + " Mins";
      } else {
        return h + (h > 1 ? " Hrs " : " Hr, ") + m + " Mins";
      }
    }
