    getDropDownDatas() {
      let payload = {
        "orgId": this.sso.getOrgId()
      };
      if (this.reportType != 'Software') {
        this.loading = true;
        this.reportService.getDropDownData(JSON.stringify(payload)).subscribe(response => {
          let manufacturerData = [{
            "id": '',
            "name": ' '
          }];
          let modelData = [{
            "id": '',
            "name": ' '
          }];
          let modeData = [{
            "id": '',
            "name": ' '
          }];
          if (response) {
            response.forEach(element => {
              if (element['manufacturer'] != '') {
                manufacturerData.push({
                  "id": element['manufacturer'],
                  "name": element['manufacturer']
                });
              }
              if (element['modelName'] != '') {
                modelData.push({
                  "id": element['modelName'],
                  "name": element['modelName']
                });
              }
              if (element['opMode'] != '') {
                modeData.push({
                  "id": element['opMode'],
                  "name": element['opMode']
                });
              }
            });
          }
          manufacturerData = this.getUniqueData(manufacturerData);
          modelData = this.getUniqueData(modelData);
          modeData = this.getUniqueData(modeData);
          this.dropdwonOutData = {
            "manufacturer": manufacturerData,
            "model": modelData,
            "mode": modeData
          };
        }, err => {
          this.loading = true;
          this.pageErrorHandle(err);
        });
      }
    }
    getUniqueData(elements) {
      let uniqueArr = [];
      for (var i = 0; i < elements.length; i++) {
        let existed = false;
        for (var j = 0; j < uniqueArr.length; j++) if (elements[i].name === uniqueArr[j].name) {
          existed = true;
          break;
        }
        if (existed) continue;
        uniqueArr.push(elements[i]);
      }
      return uniqueArr;
    }
